 43	 statusBar.setAttribute('role', 'button');
 44	 statusBar.setAttribute('tabindex', '0');
 45	 statusBar.setAttribute('aria-label', 'Click to view Vagaro system status page');
 46	 statusBar.setAttribute('title', 'Click to view Vagaro system status page');
 47	 
 48	 // Apply inline styles based on status indicator
 49	 switch (data.status.indicator) {
 50	 case 'minor':
 51	 if (statusIcon) {
 52	 statusIcon.style.backgroundColor = '#f6d644';
 53	 }
 54	 statusBar.style.backgroundColor = '#f6d644';
 55	 break;
 56	 case 'major':
 57	 if (statusIcon) {
 58	 statusIcon.style.backgroundColor = '#d83f34';
 59	 }
 60	 statusBar.style.backgroundColor = '#d83f34';
 61	 break;
 62	 case 'critical':
 63	 if (statusIcon) {
 64	 statusIcon.style.backgroundColor = '#d83f34';
 65	 }
 66	 statusBar.style.backgroundColor = '#d83f34';
 67	 break;
 68	 case 'maintenance':
 69	 if (statusIcon) {
 70	 statusIcon.style.backgroundColor = '#3498db';
 71	 }
 72	 statusBar.style.backgroundColor = '#3498db';
 73	 break;
 74	 case 'none':
 75	 default:
 76	 // Operational status - use default CSS styling
 77	 if (statusIcon) {
 78	 statusIcon.style.backgroundColor = '#379B55';
 79	 }
 80	 statusBar.style.backgroundColor = '#D7EBDD';
 81	 break;
 82	 }
 83	 } catch (error) {
 84	 console.error('Error updating status:', error);
 85	 statusMessage.textContent = 'Status information unavailable';
 86	 }
 87	 };
 88	
 89	 // Run the update immediately
 90	 updateStatusIndicator();
 91	
