 44	 const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));
 45	
 46	 // Set loading state
 47	 const setLoadingState = () => {
 48	 if (isLoading) return;
 49	
 50	 isLoading = true;
 51	 statusMessage.textContent = 'Checking system status...';
 52	
 53	 // Set a timeout to show fallback message if loading takes too long
 54	 loadingTimeoutId = setTimeout(() => {
 55	 if (isLoading) {
 56	 console.warn('[StatusIO] Loading timeout reached, showing fallback');
 57	 setFallbackState();
 58	 }
 59	 }, CONFIG.loadingTimeout);
 60	 };
 61	
 62	 // Clear loading state
 63	 const clearLoadingState = () => {
 64	 isLoading = false;
 65	 if (loadingTimeoutId) {
 66	 clearTimeout(loadingTimeoutId);
 67	 loadingTimeoutId = null;
 68	 }
 69	 };
 70	
 71	 // Set error state
 72	 const setErrorState = (error) => {
 73	 clearLoadingState();
 74	 console.error('[StatusIO] Error updating status:', error);
 75	 statusMessage.textContent = 'Status information unavailable';
 76	
 77	 // Apply default styling for error state
 78	 if (statusIcon) {
 79	 statusIcon.style.backgroundColor = '#6c757d'; // Gray for unknown
 80	 }
 81	 statusBar.style.backgroundColor = '#f8f9fa'; // Light gray background
 82	 };
 83	
 84	 // Set fallback state when API is consistently unavailable
 85	 const setFallbackState = () => {
 86	 clearLoadingState();
 87	 console.warn('[StatusIO] Using fallback state');
 88	 statusMessage.textContent = 'Visit status page for latest updates';
 89	
 90	 // Apply neutral styling
 91	 if (statusIcon) {
 92	 statusIcon.style.backgroundColor = '#6c757d';
 93	 }
 94	 statusBar.style.backgroundColor = '#f8f9fa';
 95	 };
 96	
 97	 // Apply status styling based on indicator
 98	 const applyStatusStyling = (indicator) => {
 99	 // Reset status bar classes and apply default styling
 100	 statusBar.className = 'statusio-bar w-full z-0';
 101	 statusBar.setAttribute('role', 'button');
 102	 statusBar.setAttribute('tabindex', '0');
 103	 statusBar.setAttribute('aria-label', 'Click to view Vagaro system status page');
 104	 statusBar.setAttribute('title', 'Click to view Vagaro system status page');
 105	
 106	 // Apply inline styles based on status indicator
 107	 switch (indicator) {
 108	 case 'minor':
 109	 if (statusIcon) {
 110	 statusIcon.style.backgroundColor = '#f6d644';
 111	 }
 112	 statusBar.style.backgroundColor = '#f6d644';
 113	 break;
 114	 case 'major':
 115	 if (statusIcon) {
 116	 statusIcon.style.backgroundColor = '#d83f34';
 117	 }
 118	 statusBar.style.backgroundColor = '#d83f34';
 119	 break;
 120	 case 'critical':
 121	 if (statusIcon) {
 122	 statusIcon.style.backgroundColor = '#d83f34';
 123	 }
 124	 statusBar.style.backgroundColor = '#d83f34';
 125	 break;
 126	 case 'maintenance':
 127	 if (statusIcon) {
 128	 statusIcon.style.backgroundColor = '#3498db';
 129	 }
 130	 statusBar.style.backgroundColor = '#3498db';
 131	 break;
 132	 case 'none':
 133	 default:
 134	 // Operational status - use default CSS styling
 135	 if (statusIcon) {
 136	 statusIcon.style.backgroundColor = '#379B55';
 137	 }
 138	 statusBar.style.backgroundColor = '#D7EBDD';
 139	 break;
 140	 }
 141	 };
 142	
 143	 // Fetch status with timeout and retry logic
 144	 const fetchStatusWithRetry = async (attempt = 1) => {
 145	 const controller = new AbortController();
 146	 const timeoutId = setTimeout(() => controller.abort(), CONFIG.timeout);
 147	
 148	 try {
 149	 console.log(`[StatusIO] Fetching status (attempt ${attempt}/${CONFIG.maxRetries})`);
 150	
 151	 const response = await fetch(statusioApiUrl, {
 152	 signal: controller.signal,
 153	 headers: {
 154	 'Cache-Control': 'no-cache',
 155	 },
 156	 });
 157	
 158	 clearTimeout(timeoutId);
 159	
 160	 if (!response.ok) {
 161	 throw new Error(`HTTP ${response.status}: ${response.statusText}`);
 162	 }
 163	
 164	 const data = await response.json();
 165	
 166	 if (!data || !data.status) {
 167	 throw new Error('Invalid response format: missing status data');
 168	 }
 169	
 170	 return data;
 171	
 172	 } catch (error) {
 173	 clearTimeout(timeoutId);
 174	
 175	 // Handle different types of errors
 176	 if (error.name === 'AbortError') {
 177	 throw new Error(`Request timeout after ${CONFIG.timeout}ms`);
 178	 }
 179	
 180	 if (error instanceof TypeError && error.message.includes('fetch')) {
 181	 throw new Error('Network error: Unable to connect to status API');
 182	 }
 183	
 184	 throw error;
 185	 }
 186	 };
 187	
 188	 // Main update function with retry logic
 189	 const updateStatusIndicator = async () => {
 190	 // Don't start a new update if one is already in progress
 191	 if (isLoading) {
 192	 console.log('[StatusIO] Update already in progress, skipping');
 193	 return;
 194	 }
 195	
 196	 setLoadingState();
 197	 retryCount = 0;
 198	
 199	 while (retryCount < CONFIG.maxRetries) {
 200	 try {
 201	 const data = await fetchStatusWithRetry(retryCount + 1);
 202	
 203	 // Success! Update the UI
 204	 clearLoadingState();
 205	 retryCount = 0; // Reset retry count on success
 206	
 207	 statusMessage.textContent = data.status.description;
 208	 applyStatusStyling(data.status.indicator);
 209	
 210	 console.log('[StatusIO] Status updated successfully:', data.status.description);
 211	 return;
 212	
 213	 } catch (error) {
 214	 retryCount++;
 215	 console.warn(`[StatusIO] Attempt ${retryCount} failed:`, error.message);
 216	
 217	 if (retryCount < CONFIG.maxRetries) {
 218	 // Calculate exponential backoff delay
 219	 const delay = CONFIG.retryDelay * Math.pow(2, retryCount - 1);
 220	 console.log(`[StatusIO] Retrying in ${delay}ms...`);
 221	 await sleep(delay);
 222	 } else {
 223	 // All retries exhausted
 224	 console.error('[StatusIO] All retry attempts failed');
 225	 setErrorState(error);
 226	 return;
 227	 }
 228	 }
 229	 }
 230	 };
 231	
 232	// Export default for ES module
 233	export default {};
 234	

