 64	class SourceYmlTransform extends Transform {
 65	 #defaultContent = {
 66	 title: "",
 67	 packages: [PACKAGE_NAME],
 68	 parts: [],
 69	 };
 70	
 71	 #counters = {
 72	 added: 0,
 73	 obsolete: 0,
 74	 mismatch: 0,
 75	 };
 76	
 77	 constructor() {
 78	 super({ objectMode: true });
 79	 }
 80	
 81	 _transform(file, encoding, done) {
 82	 try {
 83	 const strings = JSON.parse(file.contents.toString(encoding));
 84	 const outputContent = this.#getSourceYmlContent();
 85	
 86	 // Mark removed keys as obsolete
 87	 for (const { translation } of outputContent.parts) {
 88	 if (!(translation.key in strings) && !translation.obsolete) {
 89	 translation.obsolete = this.#getObsoleteDate();
 90	 this.#counters.obsolete++;
 91	 }
 92	 }
...
