 244	
 245	const ListItem = React.forwardRef(
 246	 ({ className, children, title, ...props }, forwardedRef) => (
 247	 <li>
 248	 <NavigationMenu.Link asChild>
 249	 <a
 250	 className={classNames("ListItemLink", className)}
 251	 {...props}
 252	 ref={forwardedRef}
 253	 >
 254	 <div className="ListItemHeading">{title}</div>
 255	 <p className="ListItemText">{children}</p>
 256	 </a>
 257	 </NavigationMenu.Link>
 258	 </li>
 259	 )
 260	);
 261	
 262	ListItem.displayName = "ListItem";
 263	ListItem.propTypes = {
 264	 className: PropTypes.string,
 265	 children: PropTypes.node,
 266	 title: PropTypes.string,
 267	};
