 2	if (window.location.pathname === '/hc/en-us/' || window.location.pathname === '/hc/en-us') {
 3	 document.addEventListener('DOMContentLoaded', function() {
 4	 const statusBar = document.getElementById('statusio-bar');
 5	 const statusMessage = document.getElementById('statusio-message');
 6	 const statusIcon = document.querySelector('.statusio-icon');
 7	 
 8	 if (!statusBar || !statusMessage) return;
 9	 
 10	 // Status API endpoint and status page URL
 11	 const statusioApiUrl = 'https://status.vagaro.com/api/v2/status.json';
 12	 const statusPageUrl = 'https://status.vagaro.com/';
 13	 
 14	 // Make status bar clickable
 15	 statusBar.addEventListener('click', function() {
 16	 window.open(statusPageUrl, '_blank');
 17	 });
 18	 
 19	 // Add keyboard accessibility
