 28	 const updateStatusIndicator = async () => {
 29	 try {
 30	 const response = await fetch(statusioApiUrl);
 31	 const data = await response.json();
 32	 
 33	 if (!data || !data.status) {
 34	 statusMessage.textContent = 'Status information unavailable';
 35	 return;
 36	 }
 37	 
 38	 // Update the status message
 39	 statusMessage.textContent = data.status.description;
 40	 
