 1	/**
 2	 * Footer Links Utility
 3	 *
 4	 * Fetches and renders footer links from Hygraph CMS endpoint
 5	 */
 6	
 7	class FooterLinksManager {
 8	 constructor(endpoint, options = {}) {
 9	 this.endpoint = endpoint;
 10	 this.options = {
 11	 timeout: 10000,
 12	 retries: 3,
 13	 ...options,
 14	 };
 15	 }
 16	
 17	 /**
 18	 * GraphQL query for footer links
 19	 */
 20	 getFooterLinksQuery() {
 21	 return {
 22	 query: `
 23	 query GetFooterLinks {
 24	 navigationMenu(
 25	 where: { id: "cm3eu68fras240dn16jbq7bz5" }
 26	 ) {
 27	 id
 28	 getStartedItems {
 29	 id
 30	 name
 31	 link
 32	 externalLink
 33	 }
 34	 companyItems {
 35	 id
 36	 name
 37	 link
 38	 externalLink
 39	 }
 40	 resourcesItems {
 41	 id
 42	 name
 43	 link
 44	 externalLink
 45	 }
 46	 }
 47	 }
 48	 `,
 49	 };
 50	 }
 51	
 52	 /**
 53	 * Fetch footer links from Hygraph
 54	 */
 55	 async fetchFooterLinks() {
 56	 try {
 57	 const response = await fetch(this.endpoint, {
 58	 method: "POST",
 59	 headers: {
 60	 "Content-Type": "application/json",
 61	 },
 62	 body: JSON.stringify(this.getFooterLinksQuery()),
 63	 signal: AbortSignal.timeout(this.options.timeout),
 64	 });
 65	
 66	 if (!response.ok) {
 67	 throw new Error(`HTTP error! status: ${response.status}`);
 68	 }
 69	
 70	 const data = await response.json();
 71	
 72	 if (data.errors) {
 73	 throw new Error(`GraphQL errors: ${JSON.stringify(data.errors)}`);
 74	 }
 75	
 76	 return data.data.navigationMenu;
 77	 } catch (error) {
 78	 console.error("Failed to fetch footer links:", error);
 79	 throw error;
 80	 }
 81	 }
