Path: assets/article-toc.js
 1	/**
 2	 * Article Table of Contents
 3	 * 
 4	 * This script creates a table of contents sidebar for article content by finding
 5	 * heading elements (h1, h2, h3, h4, h5, h6) and creating navigation links.
 6	 */
 7	document.addEventListener('DOMContentLoaded', function() {
 8	 // Check if this is an article page
 9	 const articleBody = document.querySelector('.article-body');
 10	 if (!articleBody) return;
 11	
 12	 // Find the TOC containers
 13	 const tocContainer = document.querySelector('#article-toc');
 14	 const tocMobileContainer = document.querySelector('#article-toc-mobile');
 15	 if (!tocContainer && !tocMobileContainer) return;
 16	
 17	 // Define excluded IDs
 18	 const excludedIds = ['important', 'note', 'notice', 'tip', 'warning'];
 19	 
 20	 // Find only top-level headings in the article body
 21	 const allHeadings = articleBody.querySelectorAll('h2');
 22	 
 23	 // Filter out headings with excluded IDs
 24	 const headings = Array.from(allHeadings).filter(heading => {
 25	 return !excludedIds.includes(heading.id);
 26	 });
 27	 
 28	 if (headings.length === 0) {
 29	 // Hide TOC if no headings found
 30	 if (tocContainer) tocContainer.style.display = 'none';
 31	 if (tocMobileContainer) tocMobileContainer.style.display = 'none';
 32	 return;
 33	 }
