 121	 OUTPUT_BANNER +
 122	 "\n" +
 123	 dump(outputContent, { quotingType: `"`, forceQuotes: true })
 124	 ),
 125	 });
 126	 this.push(virtualFile);
 127	
 128	 console.log(`String extraction completed!
 129	 Added strings: ${this.#counters.added}
 130	 Removed strings (marked as obsolete): ${this.#counters.obsolete}
 131	 Strings with mismatched value: ${this.#counters.mismatch}`);
 132	
 133	 done();
 134	 } catch (e) {
 135	 console.error(e);
 136	 process.exit(1);
 137	 }
 138	 }
 139	
 140	 #getSourceYmlContent() {
 141	 const outputPath = resolve(OUTPUT_DIR, OUTPUT_YML_FILE_NAME);
 142	 if (existsSync(outputPath)) {
 143	 return load(readFileSync(outputPath, "utf-8"));
 144	 }
 145	
 146	 return this.#defaultContent;
 147	 }
 148	
 149	 #getObsoleteDate() {
 150	 const today = new Date();
 151	 const obsolete = new Date(today.setMonth(today.getMonth() + 3));
 152	 return obsolete.toISOString().split("T")[0];
 153	 }
 154	}
