 1	// Initialize Radix Navigation Menu
 2	import { mountNavigationMenu } from './navigation-menu';
 3	
 4	export async function initRadixNavigation() {
 5	 try {
 6	 // Fetch navigation data (reuse your existing GraphQL query)
 7	 const HYGRAPH_ENDPOINT = "https://us-west-2.cdn.hygraph.com/content/cld3gw4bb0hr001ue9afzcunb/master";
 8	 
 9	 const response = await fetch(HYGRAPH_ENDPOINT, {
 10	 method: "POST",
 11	 headers: {
 12	 "Content-Type": "application/json",
 13	 },
 14	 body: JSON.stringify({
 15	 query: `
 16	 query GetNavigationMenu {
 17	 navigationMenu(where: { id: "clezyiora1akc0an0g68whmx0" }) {
 18	 beautyItems {
 19	 id
 20	 name
 21	 link
 22	 iconImage {
 23	 url
 24	 }
 25	 }
 26	 wellnessItems {
 27	 id
 28	 name
 29	 link
 30	 iconImage {
 31	 url
 32	 }
 33	 }
 34	 fitnessItems {
 35	 id
 36	 name
 37	 link
 38	 iconImage {
 39	 url
 40	 }
 41	 }
 42	 featureItems {
 43	 id
 44	 name
 45	 description
 46	 link
 47	 iconImage {
 48	 url
 49	 }
 50	 }
 51	 }
 52	 }
 53	 `,
 54	 }),
 55	 });
 56	
 57	 const data = await response.json();
 58	 
 59	 if (data.data?.navigationMenu) {
 60	 // Mount the React navigation component
 61	 mountNavigationMenu('radix-navigation-mount', data.data.navigationMenu);
 62	 console.log('[Radix Navigation] Successfully mounted');
 63	 }
 64	 } catch (error) {
 65	 console.error('[Radix Navigation] Failed to initialize:', error);
 66	 // You could fall back to your existing navigation here
 67	 }
 68	}
 69	
 70	// Initialize when DOM is ready
 71	document.addEventListener('DOMContentLoaded', () => {
 72	 initRadixNavigation();
 73	});...
