 55	 mainContent.setAttribute('tabindex', '-1');
 56	
 57	 // Find the first meaningful content element to use as main content
 58	 const contentSelectors = [
 59	 '.container',
 60	 '.section',
 61	 'main',
 62	 'article',
 63	 '.content',
 64	 'body > div:first-child'
 65	 ];
 66	
 67	 let targetElement = null;
 68	 for (const selector of contentSelectors) {
 69	 targetElement = document.querySelector(selector);
 70	 if (targetElement) break;
 71	 }
 72	
 73	 if (targetElement && targetElement.parentNode) {
 74	 targetElement.parentNode.insertBefore(mainContent, targetElement);
 75	 } else {
 76	 document.body.appendChild(mainContent);
 77	 }
 78	 }
 79	 });
