 838	 /**
 839	 * Initialize navigation menu with retry logic
 840	 */
 841	 async init() {
 842	 let attempts = 0;
 843	
 844	 while (attempts < this.options.retries) {
 845	 try {
 846	 await this.updateNavigationMenu();
 847	 return; // Success
 848	 } catch (error) {
 849	 attempts++;
 850	 if (attempts < this.options.retries) {
 851	 console.log(
 852	 `Retrying navigation menu fetch (${attempts}/${this.options.retries})...`
 853	 );
 854	 await new Promise((resolve) => setTimeout(resolve, 1000 * attempts));
 855	 } else {
 856	 console.error("Failed to load navigation menu after all retries");
 857	 this.showFallbackContent();
 858	 }
 859	 }
 860	 }
 861	 }
