 1	import { ESCAPE } from "./Keys";
 2	
 3	function toggleNavigation(toggle, menu) {
 4	 const isExpanded = menu.getAttribute("aria-expanded") === "true";
 5	 menu.setAttribute("aria-expanded", !isExpanded);
 6	 toggle.setAttribute("aria-expanded", !isExpanded);
 7	
 8	 // Toggle the hidden class
 9	 if (isExpanded) {
 10	 menu.classList.add("hidden");
 11	 } else {
 12	 menu.classList.remove("hidden");
 13	 }
 14	}
 15	
 16	function closeNavigation(toggle, menu) {
 17	 menu.setAttribute("aria-expanded", false);
 18	 toggle.setAttribute("aria-expanded", false);
 19	 menu.classList.add("hidden");
 20	 toggle.focus();
 21	}
