...
 18	
 19	// Function to mount the Radix Navigation Menu
 20	export function mountRadixNavigation() {
 21	 const mountPoint = document.getElementById("radix-navigation-root");
 22	 const fallbackNav = document.getElementById("fallback-navigation");
 23	
 24	 if (mountPoint) {
 25	 // Mount the React component with navigation data
 26	 ReactDOM.render(
 27	 <NavigationMenuDemo navigationData={window.navigationData} />,
 28	 mountPoint
 29	 );
 30	
 31	 // Hide the fallback navigation
 32	 if (fallbackNav) {
 33	 fallbackNav.style.display = "none";
 34	 }
 35	
 36	 console.log(
 37	 "[Radix Navigation] Successfully mounted with data:",
 38	 window.navigationData
 39	 );
 40	 return true;
 41	 }
 42	
 43	 console.error("[Radix Navigation] Mount point not found");
 44	 return false;
 45	}
