Path: src/modules/flash-notifications/renderFlashNotifications.tsx
 1	import { render } from "react-dom";
 2	import {
 3	 ThemeProviders,
 4	 createTheme,
 5	 FLASH_NOTIFICATIONS_KEY,
 6	} from "../shared";
 7	import type { Settings, ToastNotification } from "../shared";
 8	import { FlashNotifications } from "./FlashNotifications";
 9	
 10	export function renderFlashNotifications(
 11	 settings: Settings,
 12	 closeLabel: string
 13	) {
 14	 const flashNotifications = window.sessionStorage.getItem(
 15	 FLASH_NOTIFICATIONS_KEY
 16	 );
 17	
 18	 if (flashNotifications === null) {
 19	 return;
 20	 }
 21	
 22	 window.sessionStorage.removeItem(FLASH_NOTIFICATIONS_KEY);
 23	
 24	 try {
 25	 const parsedNotifications = JSON.parse(
 26	 flashNotifications
 27	 ) as ToastNotification[];
 28	
 29	 const container = document.createElement("div");
 30	 document.body.appendChild(container);
 31	
 32	 render(
 33	 <ThemeProviders theme={createTheme(settings)}>
 34	 <FlashNotifications
 35	 notifications={parsedNotifications}
 36	 closeLabel={closeLabel}
 37	 />
 38	 </ThemeProviders>,
 39	 container
 40	 );
 41	 } catch (e) {
 42	 console.error("Cannot render flash notifications", e);
 43	 }
 44	}
