Path: src/modules/shared/garden-theme/createTheme.ts
 1	import type { IButtonProps } from "@zendeskgarden/react-buttons";
 2	import type { IGardenTheme } from "@zendeskgarden/react-theming";
 3	import { DEFAULT_THEME } from "@zendeskgarden/react-theming";
 4	import { css } from "styled-components";
 5	
 6	export interface Settings {
 7	 background_color: string;
 8	 text_color: string;
 9	 brand_color: string;
 10	 brand_text_color: string;
 11	 link_color: string;
 12	 hover_link_color: string;
 13	 visited_link_color: string;
 14	}
 15	
 16	export function createTheme(settings: Settings): IGardenTheme {
 17	 return {
 18	 ...DEFAULT_THEME,
 19	 rtl: document.dir === "rtl",
 20	 colors: {
 21	 ...DEFAULT_THEME.colors,
 22	 background: settings.background_color,
 23	 foreground: settings.text_color,
 24	 primaryHue: settings.brand_color,
 25	 },
 26	 components: {
 27	 "buttons.anchor": css`
 28	 color: ${settings.link_color};
 29	
 30	 :hover,
 31	 :active,
 32	 :focus {
 33	 color: ${settings.hover_link_color};
 34	 }
 35	
 36	 &:visited {
 37	 color: ${settings.visited_link_color};
 38	 }
 39	 `,
 40	 "buttons.button": css`
 41	 ${(props: IButtonProps) =>
 42	 props.isPrimary &&
 43	 css`
 44	 color: ${settings.brand_text_color};
 45	 `}
 46	 `,
 47	 },
 48	 };
 49	}
