Path: bin/extract-strings.mjs
 1	/* eslint-env node */
 2	/**
 3	 * This script is used for the internal Zendesk translation system, and it creates or updates the source YAML file for translations,
 4	 * extracting the strings from the source code.
 5	 *
 6	 * It searches for i18next calls (`{t("my-key", "My value")}`) and it adds the strings to the specified YAML file
 7	 * (if not already present) with empty title and screenshot.
 8	 * ```yaml
 9	 * - translation:
 10	 * key: "my-key"
 11	 * title: ""
 12	 * screenshot: ""
 13	 * value: "My value"
 14	 * ```
 15	 *
 16	 * If a string present in the source YAML file is not found in the source code, it will be marked as obsolete
 17	 *
 18	 * If the value in the YAML file differs from the value in the source code, a warning will be printed in the console,
 19	 * since the script cannot know which one is correct and cannot write back in the source code files. This can happen for
 20	 * example after a "reverse string sweep", and can be eventually fixed manually.
 21	 *
 22	 * The script uses the i18next-parser library for extracting the strings and it adds a custom transformer for creating
 23	 * the file in the required YAML format.
 24	 */
 25	// Usage: node bin/extract-strings.mjs [PACKAGE_NAME] [SOURCE_FILES_GLOB] [OUTPUT_PATH]
 26	// Example: node bin/extract-strings.mjs new-request-form 'src/modules/new-request-form/**/*.{ts,tsx}' src/modules/new-request-form/translations
 27	import vfs from "vinyl-fs";
 28	import Vinyl from "vinyl";
 29	import { transform as I18NextTransform } from "i18next-parser";
 30	import { Transform } from "node:stream";
 31	import { existsSync, readFileSync } from "node:fs";
 32	import { load, dump } from "js-yaml";
 33	import { resolve } from "node:path";
 34	
 35	const PACKAGE_NAME = process.argv[2];
 36	const INPUT_GLOB = `${process.cwd()}/${process.argv[3]}`;
 37	const OUTPUT_DIR = resolve(process.cwd(), process.argv[4]);
 38	
 39	const OUTPUT_YML_FILE_NAME = "en-us.yml";
...
 50	
 51	/** @type {import("i18next-parser").UserConfig} */
 52	const config = {
 53	 // Our translation system requires that we add ".zero", ".one", ".other" keys for plurals
 54	 // i18next-parser extracts plural keys based on the target locale, so we are passing a
 55	 // locale that need exactly the ".zero", ".one", ".other" keys, even if we are extracting English strings
 56	 locales: ["lv"],
 57	 keySeparator: false,
 58	 namespaceSeparator: false,
 59	 pluralSeparator: ".",
 60	 // This path is used only as a Virtual FS path by i18next-parser, and it doesn't get written on the FS
 61	 output: "locales/en.json",
 62	};
 63	
 64	class SourceYmlTransform extends Transform {
 65	 #defaultContent = {
 66	 title: "",
 67	 packages: [PACKAGE_NAME],
 68	 parts: [],
 69	 };
 70	
 71	 #counters = {
 72	 added: 0,
 73	 obsolete: 0,
 74	 mismatch: 0,
 75	 };
 76	
 77	 constructor() {
 78	 super({ objectMode: true });
 79	 }
 80	
 81	 _transform(file, encoding, done) {
 82	 try {
 83	 const strings = JSON.parse(file.contents.toString(encoding));
 84	 const outputContent = this.#getSourceYmlContent();
 85	
 86	 // Mark removed keys as obsolete
 87	 for (const { translation } of outputContent.parts) {
 88	 if (!(translation.key in strings) && !translation.obsolete) {
 89	 translation.obsolete = this.#getObsoleteDate();
 90	 this.#counters.obsolete++;
 91	 }
 92	 }
...
 117	
 118	 const virtualFile = new Vinyl({
 119	 path: OUTPUT_YML_FILE_NAME,
 120	 contents: Buffer.from(
 121	 OUTPUT_BANNER +
 122	 "\n" +
 123	 dump(outputContent, { quotingType: `"`, forceQuotes: true })
 124	 ),
 125	 });
 126	 this.push(virtualFile);
 127	
 128	 console.log(`String extraction completed!
 129	 Added strings: ${this.#counters.added}
 130	 Removed strings (marked as obsolete): ${this.#counters.obsolete}
 131	 Strings with mismatched value: ${this.#counters.mismatch}`);
 132	
 133	 done();
 134	 } catch (e) {
 135	 console.error(e);
 136	 process.exit(1);
 137	 }
 138	 }
 139	
 140	 #getSourceYmlContent() {
 141	 const outputPath = resolve(OUTPUT_DIR, OUTPUT_YML_FILE_NAME);
 142	 if (existsSync(outputPath)) {
 143	 return load(readFileSync(outputPath, "utf-8"));
 144	 }
 145	
 146	 return this.#defaultContent;
 147	 }
 148	
 149	 #getObsoleteDate() {
 150	 const today = new Date();
 151	 const obsolete = new Date(today.setMonth(today.getMonth() + 3));
 152	 return obsolete.toISOString().split("T")[0];
 153	 }
 154	}
