Path: bin/update-modules-translations.mjs
 1	/* eslint-env node */
 2	/**
 3	 * This script is used for downloading the latest Zendesk official translation files for the modules in the `src/module` folder.
 4	 *
 5	 */
 6	import { writeFile } from "node:fs/promises";
 7	import { resolve } from "node:path";
 8	
 9	/**
 10	 * Maps each folder in the `src/modules` directory with its package name on the translation system
 11	 */
 12	const MODULES = {
 13	 "new-request-form": "new-request-form",
 14	};
 15	
 16	const BASE_URL = `https://static.zdassets.com/translations`;
 17	
 18	async function writeLocaleFile(name, filePath, outputDir) {
 19	 const response = await fetch(`${BASE_URL}${filePath}`);
 20	 const { translations } = await response.json();
 21	 const outputPath = resolve(outputDir, `${name.toLocaleLowerCase()}.json`);
 22	 await writeFile(
 23	 outputPath,
 24	 JSON.stringify(translations, null, 2) + "\n",
 25	 "utf-8"
 26	 );
 27	}
 28	
 29	async function fetchModuleTranslations(moduleName, packageName) {
 30	 try {
 31	 const manifestResponse = await fetch(
 32	 `${BASE_URL}/${packageName}/manifest.json`
 33	 );
 34	 console.log(`Downloading translations for ${moduleName}...`);
 35	
 36	 const outputDir = resolve(
 37	 process.cwd(),
 38	 `src/modules/${moduleName}/translations/locales`
 39	 );
 40	
 41	 const { json } = await manifestResponse.json();
 42	
 43	 await Promise.all(
 44	 json.map(({ name, path }) => writeLocaleFile(name, path, outputDir))
