Path: src/modules/flash-notifications/FlashNotifications.tsx
 1	import {
 2	 Title,
 3	 Notification,
 4	 Close,
 5	 useToast,
 6	} from "@zendeskgarden/react-notifications";
 7	import type { ReactElement } from "react";
 8	import { useEffect } from "react";
 9	import type { ToastNotification } from "../shared/notifications/ToastNotification";
 10	
 11	interface FlashNotificationsProps {
 12	 notifications: ToastNotification[];
 13	 closeLabel: string;
 14	}
 15	
 16	export function FlashNotifications({
 17	 notifications,
 18	 closeLabel,
 19	}: FlashNotificationsProps): ReactElement {
 20	 const { addToast } = useToast();
 21	
 22	 useEffect(() => {
 23	 for (const notification of notifications) {
 24	 const { type, title, message } = notification;
 25	 addToast(({ close }) => (
 26	 <Notification type={type}>
 27	 {title && <Title>{title}</Title>}
 28	 {message}
 29	 <Close aria-label={closeLabel} onClick={close} />
 30	 </Notification>
 31	 ));
 32	 }
 33	 }, [addToast, notifications, closeLabel]);
 34	
 35	 return <></>;
 36	}
