 1	import i18next from "i18next";
 2	import { initReactI18next } from "react-i18next";
 3	
 4	export function initI18next(locale: string) {
 5	 i18next.use(initReactI18next).init({
 6	 resources: {
 7	 [`${locale}`]: {},
 8	 },
 9	 lng: locale,
 10	 lowerCaseLng: true,
 11	 interpolation: {
 12	 escapeValue: false,
 13	 },
 14	 keySeparator: false,
 15	 pluralSeparator: ".",
 16	 });
 17	}
