 1	import i18next from "i18next";
 2	
 3	export async function loadTranslations(
 4	 locale: string,
 5	 dynamicImport: () => Promise<{ default: Record<string, string> }>
 6	) {
 7	 try {
 8	 const { default: translations } = await dynamicImport();
 9	 i18next.addResourceBundle(locale, "translation", translations);
 10	 } catch (e) {
 11	 console.error(`Cannot load translations for ${locale}`);
 12	 }
 13	}
